<script>
    document.addEventListener('DOMContentLoaded', function () {
        if (typeof Chart === 'undefined') {
            console.error('Chart.js tidak dimuat!');
            return;
        }

        const ctx = document.getElementById('orderChart');
        if (!ctx) {
            console.error('Canvas dengan ID orderChart tidak ditemukan!');
            return;
        }

        try {
            const chart = new Chart(ctx, {
                type: 'line',
                data: {
                    labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Agu', 'Sep', 'Okt', 'Nov', 'Des'],
                    datasets: [
                        {
                            label: 'Pesanan Selesai',
                            data: [165, 175, 185, 200, 245, 230, 175, 150, 145, 155, 180, 140],
                            borderColor: '#8BC34A',
                            backgroundColor: '#8BC34A',
                            tension: 0.3,
                        },
                        {
                            label: 'Pesanan Dibatalkan',
                            data: [80, 115, 105, 100, 120, 75, 95, 110, 90, 80, 135, 105],
                            borderColor: '#F44336',
                            backgroundColor: '#F44336',
                            tension: 0.3,
                        }
                    ]
                },
                options: {
                    responsive: true,
                    maintainAspectRatio: false,
                    interaction: {
                        intersect: false,
                        mode: 'index'
                    },
                    plugins: {
                        legend: { 
                            display: true,
                            position: 'bottom',
                            align: 'center',
                            labels: {
                                usePointStyle: true,
                                pointStyle: 'circle',
                                padding: 20,
                                font: {
                                    size: 12
                                },
                                color: '#666'
                            }
                        }
                    },
                    scales: {
                        x: {
                            grid: {
                                display: false
                            },
                            ticks: {
                                color: '#666',
                                font: { size: 11 }
                            }
                        },
                        y: {
                            grid: {
                                display: false
                            },
                            ticks: {
                                stepSize: 50,
                                color: '#666',
                                font: { size: 11 }
                            }
                        }
                    },
                    elements: {
                        point: { 
                            radius: 0, 
                            hoverRadius: 6,
                            hitRadius: 10
                        }
                    }
                }
            });
            console.log('Chart berhasil dibuat!', chart);
        } catch (error) {
            console.error('Error membuat chart:', error);
        }
    });
</script>
@endpush
